import React, { useState, useEffect } from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll'
import ErrorBoundary from '../components/ErrorBoundary';
import './App.css';


function App() {

    const [pokedex,setPokedex]=useState([]);
    const [searchfield, setSearchfield] = useState('');

    const getData=()=>{
      fetch('https://cors-anywhere.herokuapp.com/https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json'
      ,{
        headers : { 
          'Content-Type': 'application/json',
          'Accept': 'application/json'
         }
      }
      )
        .then(response => response.json())
        .then(pokedex => setPokedex(pokedex.pokemon));
    }
    useEffect(()=>{
      getData()
    },[])


    const onSearchChange = (event) => setSearchfield(event.target.value);

    const filteredpokedex = pokedex.filter(pokemon => {
        return pokemon.name.toLowerCase().includes(searchfield.toLowerCase());
    });

    return !pokedex.length ? 
    <h1 className='tc'>Loading...</h1> :
    (
        <div className='tc'>
            <h1 className='title'>Pokedex</h1>
            <SearchBox searchChange={onSearchChange}/>
            <Scroll>
                <ErrorBoundary>
                    <CardList pokedex={filteredpokedex} />
                </ErrorBoundary>
            </Scroll>
        </div>
    );
}

export default App;
